function Reloj(){
  const [clockState, setClockState] = useState();

useEffect (()=>{ 
    setInterval(() => {
      const date = new Date();
      setClockState(date.toLocaleTimeString());
    }, 1000);
  }, []);
  

  /*
//Se obtiene la fecha actual y se divide en fragmentos para mostrar en el diseño luego
/////
