import React, {Component,useEffect, useRef, useState} from 'react';
import logo from './img/logo.jpg';
import Reloj from './componentes/Reloj';
import Keydown, {Keys} from 'react-keydown';
import Alerta from './componentes/Detectar_Click';
import './css/App.css';



/*
function useEnterKey(key, cb){
  const callbackRef = useRef(cb);

  useEffect(()=> {
    callbackRef.current = cb;
  });

  useEffect(()=>{
    function handle(event){
      if(event.code ===key){
        callbackRef.current(event);
      }
    }

    document.addEventListener("keypress",handle);
    return()=>document.removeEventListener("keypress",handle);

  },[key]);
}*/


function App (){

function handleEnter(){
  console.log("Enter presionado");
}
  useEnterKey("Enter", handleEnter)

//se declaran variables para envío de datos del MVC
const [codigo_Barra, setCodigo_Barra] = useState(0);
const [hora_marcaje, setHora_Marcaje]= useState("");

const mostrarinfo =() =>{console.log(codigo_Barra + hora_marcaje);};

//hace focus al texbox que obtendrá la variable del codigo barras
  const focusDiv = useRef();
useEffect(()=>{
  if(focusDiv.current)focusDiv.current.focus();
},[focusDiv]);

//se manda a llamar al archivo .js en este caso es el <index/>
//se le da nombre a la clase, en este caso App
  return(

    <main>

    <form action="" >

    <div class ="img_logo">
      <img src={logo}/>
    </div>

    <div class ="mostrar_reloj">
    <Reloj></Reloj>
    </div>

    <div class="nombre_emp">
      <label id="nombre">Nombre Empleado</label>
    </div>


      <div class="prueba" >

        <input type="obtienecodigo" placeholder='codigo' onKeyPress={mostrarinfo} ref={focusDiv} onChange={(evt) => {setCodigo_Barra(evt.target.value);}} />

      </div>

      <div>
        <button onClick={mostrarinfo}>Prueba</button>
      </div>

      </form>

    </main>
  )


}







  export default App;
