const date = new Date();

function App (){


  const [dateTime, setDateTime] = useState({
    hours: date.getHours(),
    minutes: date.getMinutes(),
    seconds: date.getSeconds()
  });
  useEffect(() => {
    const timer = setInterval(() => {
      const date = new Date();

      setDateTime({
        hours: date.getHours(),
        minutes: date.getMinutes(),
        seconds: date.getSeconds(),
        setHora_Marcaje:(hours+""+minutes+""+seconds)
      });
    }, 1000);

    return () => clearInterval(timer);
  }, []);


