    </div>

      <div className="prueba" >

        <input type="text" placeholder='codigo' ref={focusDiv} onChange={(event) => {setCodigo_Barra(event.target.value);}} />

      </div>

      <div>
        <button id="probar" onClick={ingresoDatos}>Prueba</button>
      </div>

    </main>
  )

}







